INSERT INTO `SERVMASK_PREFIX_postmeta` VALUES (1703,320,'text_title','Como as Soluções para Transportadoras da Cash Berti ajudam a sua empresa a reduzir custos e melhorar sua gestão?');
INSERT INTO `SERVMASK_PREFIX_postmeta` VALUES (1704,320,'_text_title','field_652a33670b033');
INSERT INTO `SERVMASK_PREFIX_postmeta` VALUES (1705,320,'text_texto','Cash Berti é uma plataforma em carteira digital de benefícios empresariais, saldo corporativo, controle de abastecimento e gestão de frota.\r\n\r\n&nbsp;\r\n\r\nSeu uso proporciona diversas facilidades para o seu dia a dia, sem mensalidade ou custos adicionais.\r\n\r\n&nbsp;\r\n\r\nO sistema é 100% digital e foi desenvolvido com tecnologia de ponta para oferecer mais segurança, conforto, eficiência e comodidade para todos que utilizam. É possível acessar Cash Berti pelo celular ou através da integração com o sistema da sua empresa.\r\n\r\n&nbsp;\r\n\r\nToda a logística da sua frota concentrada em um só lugar.\r\n\r\n&nbsp;\r\n\r\nCom Cash Berti, a sua transportadora tem acesso a uma série de serviços integrados na mesma plataforma.\r\n\r\n&nbsp;\r\n\r\nSão diversos serviços e funcionalidades que você precisa conhecer:\r\n\r\n&nbsp;\r\n\r\n- Gestão de combustível;\r\n- Bomba interna;\r\n- Combustível de comboio (caminhão tanque);\r\n- Combustível para agronegócio;\r\n- Gestão de controle de pedágio;\r\n- Carga horária do motorista;\r\n- Rastreamento veicular;\r\n- Carta frete;\r\n- Comercialização de bombas e concentrador.\r\n\r\n&nbsp;\r\n\r\nConverse com nossos consultores e seja bem-vindo à mais nova solução digital para sua empresa.');
