INSERT INTO `SERVMASK_PREFIX_posts` VALUES (284,1,'2023-10-13 00:02:56','2023-10-13 03:02:56','<!-- wp:acf/text-block {\"name\":\"acf/text-block\",\"data\":{\"title\":\"Cash Berti. Todos os benefícios  em um só lugar.\",\"_title\":\"field_6527589cfc190\",\"text\":\"Melhorar a qualidade de vida e a eficiência das empresas parceiras através da tecnologia e de soluções financeiras inteligentes, múltiplas e abrangentes. Essa é a essência da Cash Berti.\\r\\n\\r\\n\\u0026nbsp;\\r\\n\\r\\nCriamos agilidade, confiança e segurança para o sistema financeiro das empresas, além de facilidades e benefícios para seus colaboradores.\\r\\n\\r\\n\\u0026nbsp;\\r\\n\\r\\nCash Berti é o HOJE com a cara do futuro e o conhecimento da experiência.\\r\\n\\r\\n\\u0026nbsp;\\r\\n\\r\\nSe você procura simplificar o seu dia a dia, aumentar o controle dos seus processos, reduzir custos e diminuir a burocracia, conte com a gente.\",\"_text\":\"field_65283baf08454\"},\"align\":\"\",\"mode\":\"edit\"} /-->\n\n<!-- wp:acf/media-block {\"name\":\"acf/media-block\",\"data\":{\"title\":\"Quem  Somos?\",\"_title\":\"field_6527589cfc190\",\"video_id\":\"vfgYrE0M69E\",\"_video_id\":\"field_652889583b39e\",\"text\":\"Fruto da evolução do Grupo J. Berti, a Cash Berti foi fundada em 2020. Somos catalisadores de uma transformação digital na gestão de benefícios, eliminando processos manuais e burocráticos.\\r\\n\\r\\n\\u0026nbsp;\\r\\n\\r\\nNossa plataforma é intuitiva, de fácil utilização, substituindo cartões físicos e reduzindo a complexidade administrativa para o RH.\\r\\n\\r\\n\\u0026nbsp;\\r\\n\\r\\nAlém disso, estimulamos a economia local, promovendo parcerias, conectando empresas e colaboradores, sempre com as menores taxas administrativas do mercado.\",\"_text\":\"field_652889583f64c\"},\"align\":\"\",\"mode\":\"edit\"} /-->\n\n<!-- wp:acf/latest-posts {\"name\":\"acf/latest-posts\",\"data\":{\"title\":\"Com Cash Berti, até o conteúdo chega fácil até você.\",\"_title\":\"field_6527589cfc190\",\"layout\":\"Variation\",\"_layout\":\"field_652795c5ac8bf\"},\"align\":\"\",\"mode\":\"edit\"} /-->\n\n<!-- wp:acf/icon-grid {\"name\":\"acf/icon-grid\",\"data\":{\"title\":\"Nossos pilares\",\"_title\":\"field_6527589cfc190\",\"items_0_imagem\":279,\"_items_0_imagem\":\"field_6528b06bcd8da\",\"items_0_title\":\"Tecnologia Inovadora\",\"_items_0_title\":\"field_6528b08af6413\",\"items_0_text\":\"Investir em uma plataforma tecnologicamente avançada, para oferecer uma experiência superior ao usuário, com segurança de dados e eficiência operacional. Buscar constantemente inovações para manter vantagem competitiva e atender às necessidades do mercado em constante evolução.\",\"_items_0_text\":\"field_6528b08ef6414\",\"items_1_imagem\":282,\"_items_1_imagem\":\"field_6528b06bcd8da\",\"items_1_title\":\"Excelência Operacional\",\"_items_1_title\":\"field_6528b08af6413\",\"items_1_text\":\"Buscar excelência em todas as operações, desde o desenvolvimento e manutenção da plataforma, até o suporte ao cliente. Adotar processos eficientes, monitorando métricas-chave de desempenho e buscar aprimoramentos contínuos para garantir a qualidade, segurança e confiabilidade dos serviços; conectando negócios.\",\"_items_1_text\":\"field_6528b08ef6414\",\"items_2_imagem\":281,\"_items_2_imagem\":\"field_6528b06bcd8da\",\"items_2_title\":\"Parcerias Estratégicas\",\"_items_2_title\":\"field_6528b08af6413\",\"items_2_text\":\"Estabelecer parcerias sólidas, com setor público e privado, provedores de serviços de pagamento e outras organizações do setor. As parcerias permitem oferecer ampla rede de estabelecimentos e empresas, garantindo a sustentabilidade da plataforma, além de possibilitar o desenvolvimento de soluções integradas e personalizadas aos clientes.\",\"_items_2_text\":\"field_6528b08ef6414\",\"items_3_imagem\":280,\"_items_3_imagem\":\"field_6528b06bcd8da\",\"items_3_title\":\"Foco no Cliente\",\"_items_3_title\":\"field_6528b08af6413\",\"items_3_text\":\"Colocar o cliente no centro das decisões e ações. Buscar compreender profundamente necessidades, desafios e expectativas para fornecer soluções personalizadas e excelência no atendimento ao cliente. Nossa cultura organizacional é orientada para a satisfação e fidelização dos clientes, valorizando feedbacks, e adaptando continuamente os serviços para melhor atendê-los.\",\"_items_3_text\":\"field_6528b08ef6414\",\"items\":4,\"_items\":\"field_6528b06bb1424\"},\"align\":\"\",\"mode\":\"edit\"} /-->','A Cash Berti','','inherit','closed','closed','','107-revision-v1','','','2023-10-13 00:02:56','2023-10-13 03:02:56','',107,'http://cashberti.local.site/?p=284',0,'revision','',0);
